CREATE TABLE PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO
(
    PRB_STUDENT_INFO_REQUEST_MACRO_ID   RAW(16)                 NOT NULL,
    MACRO_CODE                          VARCHAR2(4)             NOT NULL,
    MACRO_TEXT                          VARCHAR2(4000)          NOT NULL,
    CREATE_USER                         VARCHAR2(32)            NOT NULL,
    CREATE_DATE                         DATE   DEFAULT SYSDATE  NOT NULL,
    UPDATE_USER                         VARCHAR2(32)            NOT NULL,
    UPDATE_DATE                         DATE   DEFAULT SYSDATE  NOT NULL,
    CONSTRAINT PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO_PK PRIMARY KEY (PRB_STUDENT_INFO_REQUEST_MACRO_ID)
);

COMMENT ON TABLE PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO IS 'Holds macro codes and corresponding text for staff use to request more info from schools about PEN Requests.';
COMMENT ON COLUMN PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO.PRB_STUDENT_INFO_REQUEST_MACRO_ID IS 'Unique surrogate key for each macro.';
COMMENT ON COLUMN PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO.MACRO_CODE IS 'A short text code that identifies the macro and when identified will be replaced by the macro text.';
COMMENT ON COLUMN PEN_REQUEST_BATCH_STUDENT_INFO_REQUEST_MACRO.MACRO_TEXT IS 'A standard text string that will be substituted for the macro code by the application.';

